
import {Result} from "./Core"
import {Project} from "../model/Core"

export function that(b: boolean, msg?: string): Result {
    return new Result(b, msg)
}

export function equal(actual, expected, msg?: string): Result {
    if (actual == expected)
        return Result.Success
    return Result.Failure(
        msg ? 
            msg :
            `${actual} was not equal to ${expected}`
        )
}